import React, { useState } from 'react';
import './Expenses.css';
import Card from './Card';
import Expenseitems from './Expenseitems';
import ExpensesFilter from './ExpensesFilter';

function Expenses({ items }) {
  const [getYear, setGetYear] = useState('2020');
  const setGetYearData = (year) => {
    setGetYear(year);
    console.log(getYear);
  };

  return (
    <>
      <Card className='expenses'>
      <ExpensesFilter getyear={setGetYearData} />
      {items.map(({title,amount,date})=>{
        <Expenseitems title={title} amount={amount}/>
      })}

        <Expenseitems
          title={items[0].title}
          amount={items[0].amount}
          date={items[0].date}
        ></Expenseitems>
        <Expenseitems
          title={items[1].title}
          amount={items[1].amount}
          date={items[1].date}
        ></Expenseitems>
        <Expenseitems
          title={items[2].title}
          amount={items[2].amount}
          date={items[2].date}
        ></Expenseitems>
        <Expenseitems
          title={items[3].title}
          amount={items[3].amount}
          date={items[3].date}
        ></Expenseitems>
      </Card>
    </>
  );
}

export default Expenses;
