import './ExpensesForm.css';
import React, { useState } from 'react';

function ExpensesForm() {
  const [userInput, setUserInput] = useState({
    title: '',
    amount: '',
    date: '',
  });

  const saveData = (e) => {
    const { id, value } = e.target;
    setUserInput({ ...userInput, [id]: value });
  };

  const saveAllData = (e) => {
    e.preventDefalt();
    const savedData = {
      Title: use
    }
  };

  return (
    <form onSubmit={saveAllData}>
      <div className='new-expense__controls'>
        <div>
          <label className='iamlabel'>Title</label>
          <input
            name='title'
            type='text'
            className='iaminput'
            onChange={saveData}
          />
        </div>
        <div>
          <label className='iamlabel'>amount</label>
          <input
            name='amount'
            type='number'
            className='iaminput'
            onChange={saveData}
          />
        </div>
        <div>
          <label className='iamlabel'>Date</label>
          <input
            name='date'
            type='date'
            className='iaminput'
            onChange={saveData}
          />
        </div>
      </div>
      <div>
        <button className='new-expense__actions' type='submit'>
          Add List
        </button>
      </div>
    </form>
  );
}

export default ExpensesForm;
